import React, { Component } from 'react'
import EmployeeService from '../services/EmployeeService';

class CreateEmployeeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            // step 2
            appid: this.props.match.params.appid,
            title:'',
            fullName: '',
            emailId: '',
            dob: '',
            mobile:'',
            pan:'',
            aadhar:'',
        }
        this.changeTitleHandler = this.changeTitleHandler.bind(this);
        this.changeFullNameHandler = this.changeFullNameHandler.bind(this);       
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.changeTitleHandler = this.changeTitleHandler.bind(this);
        this.changeFullNameHandler = this.changeFullNameHandler.bind(this);       
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        
        this.saveOrUpdateEmployee = this.saveOrUpdateEmployee.bind(this);
    }

    // step 3
    componentDidMount(){

        // step 4
        if(this.state.id === '_add'){
            return
        }else{
            EmployeeService.getEmployeeById(this.state.appid).then( (res) =>{
                let employee = res.data;
                this.setState({
                    title: employee.title,
                    fullName: employee.fullName,
                    emailId : employee.emailId,
                    dob: employee.dob,
                    mobile: employee.mobile,
                    pan: employee.pan,
                    aadhar: employee.aadhar,
                });
            });
        }        
    }
    saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        let employee = {
            title: this.state.title,
            fullName: this.state.fullName,
            emailId: this.state.emailId,
            dob: this.state.dob,
            mobile: this.state.mobile,
            pan: this.state.pan,
            aadhar: this.state.aadhar };
        console.log('employee => ' + JSON.stringify(employee));

        // step 5
        if(this.state.id === '_add'){
            EmployeeService.createEmployee(employee).then(res =>{
                this.props.history.push('/employees');
            });
        }else{
            EmployeeService.updateEmployee(employee, this.state.appid).then( res => {
                this.props.history.push('/employees');
            });
        }
    }
    
    changeTitleHandler= (event) => {
        this.setState({fullName: event.target.value});
    }

    changeFullNameHandler= (event) => {
        this.setState({fullName: event.target.value});
    }

    changeEmailHandler= (event) => {
        this.setState({emailId: event.target.value});
    }
    changeDobHandler= (event) => {
        this.setState({dob: event.target.value});
    }
    changeMobileHandler= (event) => {
        this.setState({mobile: event.target.value});
    }
    changePanHandler= (event) => {
        this.setState({pan: event.target.value});
    }
    changeAadharHandler= (event) => {
        this.setState({aadhar: event.target.value});
    }

    cancel(){
        this.props.history.push('/employees');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Employee</h3>
        }else{
            return <h3 className="text-center">Update Employee</h3>
        }
    }
    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                {
                                    this.getTitle()
                                }
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> Title: </label>
                                            <input placeholder="Mr/Ms/Mrs" name="title" className="form-control" 
                                                value={this.state.title} onChange={this.changeTitleHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Full Name: </label>
                                            <input placeholder="Full Name" name="fullName" className="form-control" 
                                                value={this.state.fullName} onChange={this.changeFullNameHandler}/>
                                        </div>                                        
                                        <div className = "form-group">
                                            <label> Email Id: </label>
                                            <input placeholder="Email Address" name="emailId" className="form-control" 
                                                value={this.state.emailId} onChange={this.changeEmailHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> DOB: </label>
                                            <input placeholder="Last Name" name="lastName" className="form-control" 
                                                value={this.state.dob} onChange={this.changeDobHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Mobile: </label>
                                            <input placeholder="Last Name" name="lastName" className="form-control" 
                                                value={this.state.mobile} onChange={this.changeMobileHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> PAN: </label>
                                            <input placeholder="Last Name" name="lastName" className="form-control" 
                                                value={this.state.pan} onChange={this.changePanHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Aadhar: </label>
                                            <input placeholder="Last Name" name="lastName" className="form-control" 
                                                value={this.state.aadhar} onChange={this.changeAadharHandler}/>
                                        </div>


                                        <button className="btn btn-success" onClick={this.saveOrUpdateEmployee}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default CreateEmployeeComponent
